script:
// Fetch Project Name when project is selected
cur_frm.add_fetch('project',  'project_name',  'project_name');

// Setup domain on Project based on selected Customer
frappe.ui.form.on("Issue", {
	setup: function(frm) {
		frm.set_query("project", function() {
			return {
				filters: [
					["Project","customer", "in", frm.doc.customer]
				]
			};
		});
	}
});

// When customer is changed, clear the Project
frappe.ui.form.on("Issue", {
    customer: function(frm, cdt, cdn) {
        var item = locals[cdt][cdn];
        item.project = "";
        item.project_name = "";
        frm.refresh();
    },
});
